import React, { useState } from "react";

const UseStateBasics = () => {
  // console.log(useState('Hello'));
  // const value = useState(1)[0];
  // const handler = useState(1)[1];
  // console.log(value, handler);
  // we can name myValue and setMyValue anything we want
  // myValue is the value and setMyValue is the function
  // used to control this value
  const [myValue, setMyValue] = useState("Random Title");
  return <React.Fragment>useState basic example</React.Fragment>;
};

export default UseStateBasics;
