import React from "react";
import { data } from "../../../data";

const UseStateArray = () => {
  const [people, setPeople] = React.useState(data);
  return (
    <React.Fragment>
      {people.map((person) => {
        const { id, name } = person;
        return <div key={id}>{name}</div>;
      })}
    </React.Fragment>
  );
};

export default UseStateArray;
